import React from 'react';
import PropTypes from 'prop-types';
import {
  Alert,
  AlertIOS,
  Button,
  FlatList,
  StyleSheet,
  Text,
  TextInput,
  View,
  Image,
  TouchableOpacity,
} from 'react-native';
import { Venue, Client } from '../objects';
import Database from '../Database';
import { withMappedNavigationProps } from 'react-navigation-props-mapper';
import AppContainer from '../components/AppContainer';
import Styles from '../styles';
import _ from 'lodash';
import { ScrollView } from 'react-native-gesture-handler';
import { KeyboardAvoidingView, Picker } from 'react-native';
import styles from '../styles';
import { toAMPM, objectToArray, toMonthString } from '../util';
import TimeInput from '../components/TimeInput';
import { ClientStyles } from './ClientViews';
import Firebase from 'firebase';
import { Dimensions, Platform, PixelRatio } from 'react-native';
import * as Font from 'expo-font';
import {
  useFonts,
  BalsamiqSans_400Regular,
  BalsamiqSans_400Regular_Italic,
  BalsamiqSans_700Bold,
  BalsamiqSans_700Bold_Italic,
} from '@expo-google-fonts/balsamiq-sans';

@withMappedNavigationProps()
export class VenueView2 extends React.Component {
  static propTypes = {
    database: PropTypes.instanceOf(Database).isRequired,
  };

  constructor(props) {
    super(props);
  }

  _renderVenue(venue) {
    return (
      <View style={VenueStyles.entryContainer}>
        <TouchableOpacity
          style={VenueStyles.entryName}
          onPress={() => {
            this.props.navigation.navigate('Month', {
              selectedVenue: venue,
              database: this.props.database,
            });
          }}
        >
          <Text>{venue.name}</Text>
        </TouchableOpacity>
        <View style={VenueStyles.entryButton}>
          <Button
            title='⚙️🔧'
            onPress={() =>
              this.props.navigation.navigate('Venue', {
                venue: venue,
                database: this.props.database,
                onSave: (venue) => {
                  this.props.database
                    .updateVenue(venue)
                    .catch((err) => console.log(err));
                  this.forceUpdate();
                },
                onDelete: (venue) => {
                  this.props.database
                    .removeVenue(venue)
                    .catch((err) => console.log(err));
                  this.forceUpdate();
                },
              })
            }
          />
        </View>
      </View>
    );
  }

  render() {
    return (
      <AppContainer>
        <View style={Styles.contentContainer}>
          <Image
            source={require('../assets/icon-transparent.png')}
            style={VenueStyles.icon}
          />
        </View>
        <View
          style={{
            flexDirection: 'row',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <View style={{ marginTop: 10 }}>
            <Text> </Text>
          </View>
          <FlatList
            style={Styles.listContainer}
            data={this.props.database.venues.map((venue) => {
              return {
                key: venue.id,
                data: venue,
              };
            })}
            renderItem={(data) => this._renderVenue(data.item.data)}
          />
        </View>
        <View style={Styles.buttonContainer}>
          <Button
            title='Add New Venue'
            onPress={() =>
              this.props.navigation.navigate('Venue', {
                database: this.props.database,
                onSave: (venue) => {
                  this.props.database.addVenue(venue).then((venue) => {
                    this.forceUpdate();
                  });
                },
              })
            }
          />
        </View>
      </AppContainer>
    );
  }
}

@withMappedNavigationProps()
export class VenueView extends React.Component {
  static propTypes = {
    venue: PropTypes.instanceOf(Venue),
    onSave: PropTypes.func.isRequired,
    onDelete: PropTypes.func,
    database: PropTypes.instanceOf(Database).isRequired,
  };

  constructor(props) {
    super(props);

    let venue = this.props.venue || new Venue();
    this.isNew = !venue.id;
    this.state = {
      id: venue.id || '',
      name: venue.name || '',
      email: venue.contactEmail || '',
      street1: venue.address.street1 || '',
      street2: venue.address.street2 || '',
      city: venue.address.city || '',
      state: venue.address.state || '',
      zip: venue.address.zip || '',
      presetTimeSlots: venue.presetTimeSlots || [],
      artistConfirmationSendOut:
        venue.artistConfirmationSendOut.toString() || '',
      artistInvoiceSendOut: venue.artistInvoiceSendOut.toString() || '',
      monthlyBookingListSendOut:
        venue.monthlyBookingListSendOut.toString() || '',
      monthlyCalendarSendOut: venue.monthlyCalendarSendOut.toString() || '',
      emaillist: venue.emaillist || [],
    };
  }

  _renderEmailList(emaillistData) {
    let name = emaillistData.name;
    let index = parseInt(emaillistData.key) - 1;

    return (
      <EmailListEntry
        name={name}
        onSave={(newName) => {
          let emaillist = this.state.emaillist;
          emaillist[index] = newName;
          this.setState({ emaillist: emaillist });
        }}
        onDelete={() => {
          let emaillist = this.state.emaillist;
          emaillist.splice(index, 1);
          this.setState({ emaillist: emaillist });
        }}
      />
    );
  }

  _validateData() {
    // Venue is invalid it has the same name as an existing venue
    let matchingVenue = this.props.database.venues.find((venue) => {
      if (this.isNew) {
        return venue.name === this.state.name;
      } else {
        return venue.name === this.state.name && venue.id !== this.state.id;
      }
    });
    if (matchingVenue) {
      alert('There is already a venue with that name.');
      return false;
    }

    // Venue is invalid if its name is blank
    if (this.state.name === '') {
      alert('The venue must have a name.');
      return false;
    }

    // Venue is invalid if the contact email is blank
    if (this.state.email === '') {
      alert('The venue must have an email address.');
      return false;
    }

    // Venue is invalid if the contact email is improperly formatted
    let emailRegex = new RegExp(`^[\\w\.]+@(\\w{2,}\.)+\\w+$`);
    if (!emailRegex.test(this.state.email)) {
      alert('The given email address was not in the proper format.');
      return false;
    }

    // Venue is invalid if any of the monthly send out days are greater than 28 or less than 1
    if (
      this.state.artistConfirmationSendOut < 1 ||
      this.state.artistConfirmationSendOut > 28
    ) {
      alert(
        'The artist booking confirmation send out day of the month must be from 1 to 28'
      );
      return false;
    }
    if (
      this.state.artistInvoiceSendOut < 1 ||
      this.state.artistInvoiceSendOut > 28
    ) {
      alert(
        'The artist invoice send out day of the month must be from 1 to 28'
      );
      return false;
    }
    if (
      this.state.monthlyBookingListSendOut < 1 ||
      this.state.monthlyBookingListSendOut > 28
    ) {
      alert(
        'The monthly booking list send out day of the month must be from 1 to 28'
      );
      return false;
    }
    if (
      this.state.monthlyCalendarSendOut < 1 ||
      this.state.monthlyCalendarSendOut > 28
    ) {
      alert(
        'The monthly calendar send out day of the month must be from 1 to 28'
      );
      return false;
    }
    return true;
  }

  _renderTimeSlot(data) {
    let timeSlot = data.timeSlot;
    let index = parseInt(data.key);
    return (
      <TimeSlotEntry
        timeSlot={timeSlot}
        onSave={(newTimeSlot) => {
          let presetTimeSlots = [...this.state.presetTimeSlots];
          presetTimeSlots[index] = {
            start: newTimeSlot.start,
            end: newTimeSlot.end,
          };
          this.setState({ presetTimeSlots: presetTimeSlots });
        }}
        onDelete={() => {
          let presetTimeSlots = [...this.state.presetTimeSlots];
          presetTimeSlots.splice(index, 1);
          this.setState({ presetTimeSlots: presetTimeSlots });
        }}
      />
    );
  }

  render() {
    return (
      <AppContainer style={Styles.infoView}>
        <View style={Styles.contentContainer}>
          {/* NEW CODE - Kaitlin - Added ScrollView to show all settings, and Keyboard to show all input boxes */}
          <ScrollView style={VenueStyles.scrollView}>
            <KeyboardAvoidingView
              style={styles.container}
              behavior='padding'
              enabled
            >
              <Text style={Styles.infoTitle}>
                {this.isNew ? 'Create New Venue' : 'Update Venue'}
              </Text>

              {/* Venue Name Input */}
              <View style={Styles.inputRow}>
                <Text style={Styles.inputTitle}>Name</Text>
                <TextInput
                  style={Styles.inputBox}
                  value={this.state.name}
                  onChangeText={(value) => this.setState({ name: value })}
                />
              </View>

              {/* Contact Email Input */}
              <View style={Styles.inputRow}>
                <Text style={Styles.inputTitle}>Email</Text>
                <TextInput
                  style={Styles.inputBox}
                  value={this.state.email}
                  onChangeText={(value) => this.setState({ email: value })}
                />
              </View>

              {/* Street Address 1 Input */}
              <View style={Styles.inputRow}>
                <Text style={Styles.inputTitle}>Street 1</Text>
                <TextInput
                  style={Styles.inputBox}
                  value={this.state.street1}
                  onChangeText={(value) => this.setState({ street1: value })}
                />
              </View>

              {/* Street Address 2 Input */}
              <View style={Styles.inputRow}>
                <Text style={Styles.inputTitle}>Street 2</Text>
                <TextInput
                  style={Styles.inputBox}
                  value={this.state.street2}
                  onChangeText={(value) => this.setState({ street2: value })}
                />
              </View>

              {/* City Input */}
              <View style={Styles.inputRow}>
                <Text style={Styles.inputTitle}>City</Text>
                <TextInput
                  style={Styles.inputBox}
                  value={this.state.city}
                  onChangeText={(value) => this.setState({ city: value })}
                />
              </View>

              {/* State Input */}
              <View style={Styles.inputRow}>
                <View style={[Styles.inputRow, VenueStyles.stateContainer]}>
                  <Text style={[Styles.inputTitle, VenueStyles.stateTitle]}>
                    State
                  </Text>
                  <TextInput
                    style={[Styles.inputBox, VenueStyles.stateInput]}
                    value={this.state.state}
