class TimeSlotEntry extends React.Component {
  static propTypes = {
    timeSlot: PropTypes.object,
    onSave: PropTypes.func.isRequired,
    onDelete: PropTypes.func.isRequired,
  };

  constructor(props) {
    super(props);
    this.state = {
      timeSlot: this.props.timeSlot || { start: '15:00', end: '17:00' },
      isEditing: !this.props.timeSlot,
    };
  }

  render() {
    if (this.state.isEditing) {
      return (
        <View>
          <View style={Styles.inputRow}>
            <View style={Styles.customStartTimeContainer}>
              <View style={Styles.inputRow}>
                <TimeInput
                  value={this.state.timeSlot.start}
                  onValueChange={(startTime) => {
                    this.setState({
                      timeSlot: {
                        start: startTime,
                        end: this.state.timeSlot.end,
                      },
                    });
                  }}
                />
              </View>
            </View>
            <View style={Styles.customEndTimeContainer}>
              <View style={Styles.inputRow}>
                <Text style={Styles.customTimeTitle}> to </Text>
                <TimeInput
                  value={this.state.timeSlot.end}
                  onValueChange={(endTime) => {
                    this.setState({
                      timeSlot: {
                        start: this.state.timeSlot.start,
                        end: endTime,
                      },
                    });
                  }}
                />
              </View>
            </View>
            <View style={ClientStyles.entryButton}>
              <Button
                title='✔️'
                color='#fff'
                onPress={() => {
                  this.setState({ isEditing: false });

                  if (
                    this.state.timeSlot.start.trim() === '' ||
                    this.state.timeSlot.end.trim() === ''
                  ) {
                    this.props.onDelete(this.state.timeSlot);
                  } else {
                    this.props.onSave(this.state.timeSlot);
                  }
                }}
              />
            </View>
            <View style={ClientStyles.entryButton}>
              <Button
                title='❌'
                color='#fff'
                onPress={() => {
                  this.props.onDelete(this.state.timeSlot);
                  this.setState({ isEditing: false });
                }}
              />
            </View>
          </View>
        </View>
      );
    } else {
      return (
        <View style={ClientStyles.entryContainer}>
          <Text style={[ClientStyles.entryName, ClientStyles.performerName]}>
            {toAMPM(this.props.timeSlot.start) +
              ' to ' +
              toAMPM(this.props.timeSlot.end)}
          </Text>
          <View style={ClientStyles.entryButton}>
            <Button
              title='✏️'
              color='#fff'
              onPress={() => {
                this.setState({ isEditing: true });
              }}
            />
          </View>
          <View style={ClientStyles.entryButton}>
            <Button
              title='❌'
              color='#fff'
              onPress={() => {
                this.props.onDelete(this.state.timeSlot);
              }}
            />
          </View>
        </View>
      );
    }
  }
}
